import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import { 
  ChevronDown, 
  ChevronUp, 
  HelpCircle, 
  ArrowRight,
  Clock,
  Users,
  Shield,
  Zap
} from 'lucide-react';

const FAQ = () => {
  const [openItems, setOpenItems] = useState({});

  const toggleItem = (index) => {
    setOpenItems(prev => ({
      ...prev,
      [index]: !prev[index]
    }));
  };

  const faqCategories = [
    {
      title: "Services & Implémentation",
      icon: <Zap className="h-6 w-6" />,
      questions: [
        {
          question: "Quels services Zoho proposez-vous ?",
          answer: "Nous proposons l'implémentation complète de Zoho CRM, Zoho Books, Zoho People, Zoho Creator, ainsi que des services de formation, support technique et intégrations personnalisées."
        },
        {
          question: "Combien de temps dure une implémentation Zoho ?",
          answer: "La durée varie selon la complexité du projet. Une implémentation CRM basique prend 2-4 semaines, tandis qu'un projet complet avec plusieurs modules peut prendre 6-12 semaines."
        },
        {
          question: "Proposez-vous des formations pour nos équipes ?",
          answer: "Oui, nous offrons des formations complètes adaptées à vos besoins spécifiques, incluant des sessions en groupe et individuelles, ainsi que des supports de formation personnalisés."
        },
        {
          question: "Pouvez-vous intégrer Zoho avec nos outils existants ?",
          answer: "Absolument ! Nous spécialisons dans les intégrations avec vos outils existants (ERP, outils marketing, systèmes de paie, etc.) pour une expérience utilisateur fluide."
        }
      ]
    },
    {
      title: "Tarifs & Devis",
      icon: <Shield className="h-6 w-6" />,
      questions: [
        {
          question: "Comment sont calculés vos tarifs ?",
          answer: "Nos tarifs sont basés sur la complexité du projet, le nombre de modules Zoho implémentés, les intégrations nécessaires et le niveau de personnalisation requis."
        },
        {
          question: "Proposez-vous un audit gratuit ?",
          answer: "Oui, nous offrons un audit gratuit de vos processus actuels pour identifier les opportunités d'amélioration et vous proposer une solution adaptée."
        },
        {
          question: "Y a-t-il des coûts cachés ?",
          answer: "Non, nous sommes transparents sur nos tarifs. Tous les coûts sont détaillés dans notre devis initial, incluant l'implémentation, la formation et le support post-lancement."
        },
        {
          question: "Proposez-vous des forfaits mensuels ?",
          answer: "Oui, nous proposons des forfaits de support et maintenance mensuels pour assurer le bon fonctionnement de vos solutions Zoho."
        }
      ]
    },
    {
      title: "Support & Maintenance",
      icon: <Users className="h-6 w-6" />,
      questions: [
        {
          question: "Quel type de support proposez-vous ?",
          answer: "Nous offrons un support technique complet par email, téléphone et visioconférence, avec un temps de réponse garanti de 24h pour les urgences."
        },
        {
          question: "Le support est-il inclus après l'implémentation ?",
          answer: "Oui, nous incluons 3 mois de support gratuit après l'implémentation, puis nous proposons des forfaits de support continu."
        },
        {
          question: "Pouvez-vous nous aider avec les mises à jour Zoho ?",
          answer: "Absolument ! Nous vous accompagnons dans toutes les mises à jour Zoho et nous testons les nouvelles fonctionnalités pour votre environnement."
        },
        {
          question: "Proposez-vous des services de maintenance préventive ?",
          answer: "Oui, nous proposons des services de maintenance préventive incluant des audits réguliers, des optimisations et des recommandations d'amélioration."
        }
      ]
    },
    {
      title: "Technique & Sécurité",
      icon: <Clock className="h-6 w-6" />,
      questions: [
        {
          question: "Nos données sont-elles sécurisées ?",
          answer: "Oui, Zoho respecte les plus hauts standards de sécurité (ISO 27001, SOC 2) et nous configurons vos solutions selon les meilleures pratiques de sécurité."
        },
        {
          question: "Pouvez-vous migrer nos données existantes ?",
          answer: "Oui, nous gérons la migration complète de vos données depuis vos systèmes existants vers Zoho, en assurant l'intégrité et la continuité."
        },
        {
          question: "Zoho est-il compatible avec nos besoins de conformité ?",
          answer: "Zoho est conforme au RGPD et à de nombreuses réglementations. Nous vous aidons à configurer les paramètres de conformité spécifiques à votre secteur."
        },
        {
          question: "Que se passe-t-il en cas de problème technique ?",
          answer: "Notre équipe technique est disponible 24/7 pour les urgences. Nous avons des procédures de sauvegarde et de récupération pour minimiser les interruptions."
        }
      ]
    }
  ];

  return (
    <div className="min-h-screen">
      {/* Hero Section */}
      <section className="bg-gradient-to-br from-primary-50 to-secondary-50 section-padding">
        <div className="container-max text-center">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
          >
            <div className="w-16 h-16 bg-primary-100 rounded-full flex items-center justify-center mx-auto mb-6">
              <HelpCircle className="h-8 w-8 text-primary-600" />
            </div>
            <h1 className="text-4xl md:text-5xl font-bold text-gray-900 mb-6">
              Questions Fréquentes
            </h1>
            <p className="text-xl text-gray-600 max-w-3xl mx-auto">
              Trouvez rapidement les réponses à vos questions sur nos services Zoho, 
              nos tarifs et notre accompagnement.
            </p>
          </motion.div>
        </div>
      </section>

      {/* FAQ Categories */}
      <section className="section-padding bg-white">
        <div className="container-max">
          <div className="space-y-12">
            {faqCategories.map((category, categoryIndex) => (
              <motion.div
                key={category.title}
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: categoryIndex * 0.1 }}
                viewport={{ once: true }}
              >
                <div className="flex items-center space-x-3 mb-8">
                  <div className="w-12 h-12 bg-primary-100 rounded-lg flex items-center justify-center">
                    {category.icon}
                  </div>
                  <h2 className="text-2xl md:text-3xl font-bold text-gray-900">
                    {category.title}
                  </h2>
                </div>

                <div className="space-y-4">
                  {category.questions.map((item, index) => {
                    const itemIndex = `${categoryIndex}-${index}`;
                    const isOpen = openItems[itemIndex];

                    return (
                      <motion.div
                        key={index}
                        initial={{ opacity: 0, y: 10 }}
                        whileInView={{ opacity: 1, y: 0 }}
                        transition={{ duration: 0.3, delay: index * 0.05 }}
                        viewport={{ once: true }}
                        className="bg-gray-50 rounded-lg border border-gray-200"
                      >
                        <button
                          onClick={() => toggleItem(itemIndex)}
                          className="w-full px-6 py-4 text-left flex items-center justify-between hover:bg-gray-100 transition-colors duration-200"
                        >
                          <span className="font-semibold text-gray-900 pr-4">
                            {item.question}
                          </span>
                          {isOpen ? (
                            <ChevronUp className="h-5 w-5 text-primary-600 flex-shrink-0" />
                          ) : (
                            <ChevronDown className="h-5 w-5 text-primary-600 flex-shrink-0" />
                          )}
                        </button>
                        {isOpen && (
                          <motion.div
                            initial={{ opacity: 0, height: 0 }}
                            animate={{ opacity: 1, height: "auto" }}
                            exit={{ opacity: 0, height: 0 }}
                            transition={{ duration: 0.3 }}
                            className="px-6 pb-4"
                          >
                            <p className="text-gray-600 leading-relaxed">
                              {item.answer}
                            </p>
                          </motion.div>
                        )}
                      </motion.div>
                    );
                  })}
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Contact CTA */}
      <section className="section-padding bg-gray-50">
        <div className="container-max text-center">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            <h2 className="text-3xl md:text-4xl font-bold text-gray-900 mb-4">
              Vous ne trouvez pas votre réponse ?
            </h2>
            <p className="text-xl text-gray-600 mb-8 max-w-2xl mx-auto">
              Notre équipe d'experts est là pour répondre à toutes vos questions 
              et vous accompagner dans votre projet Zoho.
            </p>
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <Link to="/contact" className="btn-primary inline-flex items-center">
