import React from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import { 
  Users, 
  Target, 
  Award, 
  Heart, 
  Lightbulb, 
  Shield,
  CheckCircle,
  Star,
  ArrowRight,
  Calendar
} from 'lucide-react';

const About = () => {
  const values = [
    {
      icon: <Target className="h-8 w-8" />,
      title: "Excellence",
      description: "Nous visons l'excellence dans chaque projet."
    },
    {
      icon: <Heart className="h-8 w-8" />,
      title: "Passion",
      description: "Notre passion pour Zoho nous pousse à innover."
    },
    {
      icon: <Users className="h-8 w-8" />,
      title: "Collaboration",
      description: "Nous travaillons en étroite relation avec nos clients."
    },
    {
      icon: <Lightbulb className="h-8 w-8" />,
      title: "Innovation",
      description: "Nous restons à la pointe de la technologie."
    }
  ];

  const achievements = [
    { number: "50+", label: "Projets réalisés", icon: <Award className="h-6 w-6" /> },
    { number: "98%", label: "Satisfaction client", icon: <Star className="h-6 w-6" /> },
    { number: "5+", label: "Années d'expérience", icon: <Calendar className="h-6 w-6" /> }
  ];

  return (
    <div className="min-h-screen">
      {/* Hero Section */}
      <section className="bg-gradient-to-br from-primary-50 to-secondary-50 section-padding">
        <div className="container-max">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-center">
            <motion.div
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.6 }}
            >
              <h1 className="text-4xl md:text-5xl font-bold text-gray-900 mb-6">
                À propos de{' '}
                <span className="text-primary-600">ZohoFlow</span>
              </h1>
              <p className="text-xl text-gray-600 mb-8 leading-relaxed">
                Experts passionnés en conseil et implémentation Zoho, nous accompagnons 
                les entreprises dans leur transformation digitale depuis plus de 5 ans.
              </p>
              <Link to="/contact" className="btn-primary">
                Nous contacter
              </Link>
            </motion.div>
            <motion.div
              initial={{ opacity: 0, x: 20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.6, delay: 0.2 }}
            >
              <div className="bg-white rounded-2xl shadow-xl p-8">
                <div className="space-y-6">
                  <div className="flex items-center space-x-4">
                    <div className="w-12 h-12 bg-primary-100 rounded-lg flex items-center justify-center">
                      <Award className="h-6 w-6 text-primary-600" />
                    </div>
                    <div>
                      <h3 className="font-semibold text-gray-900">Expertise reconnue</h3>
                      <p className="text-gray-600">Certifications Zoho officielles</p>
                    </div>
                  </div>
                  <div className="flex items-center space-x-4">
                    <div className="w-12 h-12 bg-green-100 rounded-lg flex items-center justify-center">
                      <Users className="h-6 w-6 text-green-600" />
                    </div>
                    <div>
                      <h3 className="font-semibold text-gray-900">Équipe expérimentée</h3>
                      <p className="text-gray-600">Plus de 20 ans d'expérience cumulée</p>
                    </div>
                  </div>
                </div>
              </div>
            </motion.div>
          </div>
        </div>
      </section>

      {/* Story Section */}
      <section className="section-padding bg-white">
        <div className="container-max">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-center">
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6 }}
              viewport={{ once: true }}
            >
              <h2 className="text-3xl md:text-4xl font-bold text-gray-900 mb-6">
                Notre Histoire
              </h2>
              <div className="space-y-6 text-gray-600">
                <p>
                  Fondée en 2019, ZohoFlow est née de la passion pour la technologie 
                  et de la conviction que Zoho peut transformer la façon dont les 
                  entreprises travaillent.
                </p>
                <p>
                  Notre équipe d'experts a accompagné plus de 50 entreprises dans 
                  leur transformation digitale, en leur offrant des solutions 
                  personnalisées et un support exceptionnel.
                </p>
              </div>
            </motion.div>
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.2 }}
              viewport={{ once: true }}
              className="grid grid-cols-1 sm:grid-cols-3 gap-4"
            >
              {achievements.map((achievement, index) => (
                <div key={index} className="bg-gray-50 rounded-xl p-6 text-center">
                  <div className="text-primary-600 mb-3 flex justify-center">
                    {achievement.icon}
                  </div>
                  <div className="text-2xl font-bold text-gray-900 mb-2">
                    {achievement.number}
                  </div>
                  <div className="text-gray-600 text-sm">
                    {achievement.label}
                  </div>
                </div>
              ))}
            </motion.div>
          </div>
        </div>
      </section>

      {/* Values Section */}
      <section className="section-padding bg-gray-50">
        <div className="container-max">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
            className="text-center mb-16"
          >
            <h2 className="text-3xl md:text-4xl font-bold text-gray-900 mb-4">
              Nos Valeurs
            </h2>
            <p className="text-xl text-gray-600 max-w-3xl mx-auto">
              Les principes qui guident notre travail et nos relations avec nos clients
            </p>
          </motion.div>

